import { $host} from "./index";


export  const sentMail = async (state)=>{
    try {
        console.log('mailSending');
        console.log(state)
        let axiosConfig = {
            headers: {
                'Content-Type': 'application/json;charset=UTF-8',
                "Access-Control-Allow-Origin": "*",
            }
        };
        const body = { a:'username', b:'password' };

        const {data} = await $host.post('api/mailer/', JSON.stringify(body),  axiosConfig).then(res => console.log(res))
            .catch(err => console.log('Login: ', err));;
        console.log('mailSend');
        return data
    } catch (e) {

        console.log(e);
        return
    }

}
