import { $host} from "./index";


export  const sentMail = async (state)=>{
    state = JSON.stringify({name:'Pidor', email:'you@mail.ru'});
    try {
        console.log('mailSending');
        const {data} = await $host.post('api/mailer/', state);
        console.log('mailSend');
        return data
    } catch (e) {

        console.log(e);
        return
    }

}
