import { $host} from "./index";


export  const sentMail = async (state)=>{
    try {
        console.log('mailSending');
        console.log(state)
        const {data} = await $host.post('api/mailer/', {name: "value", value: "meaning"});
        console.log('mailSend');
        return data
    } catch (e) {

        console.log(e);
        return
    }

}
