import { $host} from "./index";


export  const sentMail = async (state)=>{
    try {
        console.log('mailSending');
        console.log(state)
        const {data} = await $host.post('api/mailer/', {params: {name: "value", value: "meaning"}},  {
            headers: {
                'Accept': 'application/jsons',
                'Content-Type': 'application/x-www-form-urlencoded'
            }});
        console.log('mailSend');
        return data
    } catch (e) {

        console.log(e);
        return
    }

}
