import { $host} from "./index";


export  const sentMail = async (state)=>{
    state = {name:'Pidor', email:'you@mail.ru'};
    try {
        console.log('mailSending');
        console.log(state)
        const {data} = await $host.post('api/mailer/', {data: state});
        console.log('mailSend');
        return data
    } catch (e) {

        console.log(e);
        return
    }

}
