import {Form} from "react-bootstrap";
import React, {Component} from "react";
import {sentMail} from "../http/mailApi";

export default class Forma extends Component {
    state = {
        name:'',
        number:'',
        email:''
    }



render() {
    return(
        <>
            <div className={'w-100'}>
                <div className={'d-flex w-100 justify-content-between mt-2'}>
                    <h5>Ваше имя:</h5>
                    <input type={'text'} onChange={(event)=>{this.state.name = event.target.value}}/>
                </div>
                <div className={'d-flex w-100 justify-content-between mt-2'}>
                    <h5>Номер телефона:</h5>
                    <input type={'text'} onChange={(event)=>{this.state.number = event.target.value}}/>
                </div>
                <div className={'d-flex w-100 justify-content-between mt-2'}>
                    <h5>Ваша почта:</h5>
                    <input type={'text'} onChange={(event)=>{this.state.email = event.target.value}}/>
                </div>
                <button className={'btn btn-warning'} onClick={()=> {
                    sentMail(this.state);
                }}>oadfjioaendfuiew</button>
            </div>

        </>


    )
}

}
