import React, {Component} from 'react';
import About from "./About";
import Bolls from "./Bolls";
import CarouselBox from "./CarouselBox";
import Goods from "./Goods";

import Images1 from './phonePackage.svg';
import Images2 from './siteLendingPackage.svg';
import Images5 from './sitePackage.svg';


import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

function Home (){
  let width = window.innerWidth;
  let settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1
  };
  let settingsMobile = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1
  };
  if (width <= 720){
        return (
            <>

                <Slider {...settingsMobile}>
                  <div>
                    <Goods title={'Предложение    "Блог"!'} discription={'Купив "Блог", вы получите полностью рабочий информационный-рекламный сайт для создания контента определенной тематики'} price={'15900p'} source={Images1}/>
                  </div>
                  <div>
                    <Goods title={'Предложение "Магазин"!'} discription={'Купив "Магазин", вы получите готовый магазин созданный по вашим идеям нашими специалистами, он будет подключен к нужным вам службам оплаты и/или доставки и учета!'} price={'29900p'} source={Images2}/>
                  </div>
                  <div>
                    <Goods title={'Предложение "Визитка"!'} discription={'Купив "Визитку", вы получите хороший, красивый лендинг для рекламы себя и своих товаров!'} price={'от 4900p'} source={Images5}/>
                  </div>
                  <div>
                    <Goods title={'Предложение "Мобильный"!'} discription={'Купив "Мобильный", вы получите в свое распоряжение мобильное приложение нужной вам тематики. От мобильного блога, до магазина с товарами!'} price={'от 139000p'} source={Images5}/>
                  </div>
                  <div>
                    <Goods title={'Пакет "Всё"!'} price={'от 149000p'} discription={'Купив пакет "Всё", вы получите полностью функционирующий интернет-магазин и мобильное приложение аналогичной тематики'} source={Images5}/>
                  </div>

                </Slider>

                <About/>
                <CarouselBox />
            </>
        );
      };
  if (width > 720) {
    return(
      <>

      <Slider {...settings}>
        <div>
          <Goods title={'Предложение    "Блог"!'} discription={'Купив "Блог", вы получите полностью рабочий информационный-рекламный сайт для создания контента определенной тематики'} price={'15900p'} source={Images1}/>
        </div>
        <div>
          <Goods title={'Предложение "Магазин"!'} discription={'Купив "Магазин", вы получите готовый магазин созданный по вашим идеям нашими специалистами, он будет подключен к нужным вам службам оплаты и/или доставки и учета!'} price={'29900p'} source={Images2}/>
        </div>
        <div>
          <Goods title={'Предложение "Визитка"!'} discription={'Купив "Визитку", вы получите хороший, красивый лендинг для рекламы себя и своих товаров!'} price={'от 4900p'} source={Images5}/>
        </div>
        <div>
          <Goods title={'Предложение "Мобильный"!'} discription={'Купив "Мобильный", вы получите в свое распоряжение мобильное приложение нужной вам тематики. От мобильного блога, до магазина с товарами!'} price={'от 139000p'} source={Images5}/>
        </div>
        <div>
          <Goods title={'Пакет "Всё"!'} price={'от 149000p'} discription={'Купив пакет "Всё", вы получите полностью функционирующий интернет-магазин и мобильное приложение аналогичной тематики'} source={Images5}/>
        </div>

      </Slider>

      <About/>
      <CarouselBox />
      </>
    )

  }
}

export default Home;
