import {Form} from "react-bootstrap";
import React, {Component} from "react";
import {sentMail} from "../http/mailApi";

export default class Forma extends Component {
    state = {
        name:'',

    }



render() {
    return(
        <>
            <div>
                <div className={'d-flex w-100 justify-content-around'}>
                    <h4>Ваше имя:</h4>
                    <input type={'text'} title={'Ваше имя'} onChange={(event)=>{this.state.name = event.target.value}}/>
                </div>
                <button className={'btn btn-warning ml-auto mr-auto mt-5 mb-5'} onClick={()=> {
                    sentMail(this.state);
                }}>oadfjioaendfuiew</button>
            </div>

        </>


    )
}

}
